    private void cmbCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbCalcularActionPerformed
        // TODO add your handling code here:
        String  num1, resul;
        double n1 = 1;
        int ope = 0;
        
        double BonoPrimerAñoLaboral= 100000, BonoSiguienteAño = 120000;
        
        
        if (txtAñosEnLaEmpresa.getText().trim().isEmpty()){
        JOptionPane.showMessageDialog(this, "Digite el numero de años que lleva en la empresa", "Error",JOptionPane.ERROR_MESSAGE);
        txtAñosEnLaEmpresa.requestFocusInWindow();
        } else {
        
            
        n1= Double.parseDouble(txtAñosEnLaEmpresa.getText());
        }
        
        ope = (int) (100000 + (120000*n1));
        resul = String.valueOf(ope);
         txtValorAPagar.setText(resul);
        
        
    }//GEN-LAST:event_cmbCalcularActionPerformed

