    private void txtAñosEnLaEmpresaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtAñosEnLaEmpresaKeyTyped
        // TODO add your handling code here:
        char c=evt.getKeyChar(); 
             
         
          if(!Character.isDigit(c)) { 
              getToolkit().beep(); 
               
              evt.consume();
          }
    }//GEN-LAST:event_txtAñosEnLaEmpresaKeyTyped

